function addUser(){var a=$("#username").val(),b=$("#email").val(),c=$("#password").val(),d=$("#password1").val(),e=$("#isAdmin").val();$.ajax({url:"/admin/user/add",type:"post",data:JSON.stringify({username:a,email:b,password:c,password1:d,admin:e}),headers:{"Content-Type":"application/json"},dataType:"json",statusCode:{409:function(){$("#existsModal").modal("toggle")},406:function(){$("#passwordModal").modal("toggle")},202:function(){$("#successModal").modal("toggle")}}})};
